SELECT a.campaign_name AS campaignName, a.media AS media, a.contact_point AS contactPoint, a.campaign_id AS campaignId, a.start_date AS startDate, a.end_date AS endDate, a.cost AS cost, a.exposure_count AS exposureCount, a.exposure_user_count AS exposureUserCount, a.click_count AS clickCount, a.click_user_count AS clickUserCount, a.monthly_active_member_count AS monthlyActiveMemberCount, a.monthly_active_member_gmv AS monthlyActiveMemberGmv, a.new_member_acquisition_count AS newMemberAcquisitionCount, a.new_member_acquisition_gmv AS newMemberAcquisitionGmv, a.next_month_active_member_count AS nextMonthActiveMemberCount, a.next_month_active_member_gmv AS nextMonthActiveMemberGmv, a.next_month_new_member_count AS nextMonthNewMemberCount, a.next_month_new_member_gmv AS nextMonthNewMemberGmv,
ROUND(a.cost/(select sum(cost) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as costAcc, ROUND(a.exposure_count/(select sum(exposure_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as exposureCountAcc, ROUND(a.exposure_user_count/(select sum(exposure_user_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as exposureUserCountAcc, ROUND(a.click_count/(select sum(click_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as clickCountAcc, ROUND(a.click_user_count/(select sum(click_user_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as clickUserCountAcc, ROUND(a.monthly_active_member_count/(select sum(monthly_active_member_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as mamcAcc, ROUND(a.monthly_active_member_gmv/(select sum(monthly_active_member_gmv) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as mamgAcc, ROUND(a.new_member_acquisition_count/(select sum(new_member_acquisition_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as nmacAcc, ROUND(a.new_member_acquisition_gmv/(select sum(new_member_acquisition_gmv) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as nmagAcc, ROUND(a.next_month_active_member_count/(select sum(next_month_active_member_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as nmnmcaAcc, ROUND(a.next_month_active_member_count/(select sum(next_month_active_member_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as nmamgAcc, ROUND(a.next_month_new_member_count/(select sum(next_month_new_member_count) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as nmnmcAcc, ROUND(a.next_month_new_member_gmv/(select sum(next_month_new_member_gmv) from activity_media_slot_analysis where campaign_id=#{slot.cid}),2) as nmnmgAcc,
ROUND(a.cost/a.exposure_count*1000, 2) AS cpm, ROUND(a.monthly_active_member_gmv/a.cost, 2) AS roi, ROUND(a.cost/a.monthly_active_member_count, 2) AS cac, ROUND(a.monthly_active_member_count/b.periodic_monthly_active_user_count, 2) AS mamcRate, ROUND(a.monthly_active_member_gmv/b.periodic_monthly_active_user_gmv, 2) AS mamcGmvRate, ROUND(a.new_member_acquisition_gmv/a.cost, 2) AS rnmRoi, ROUND(a.cost/a.new_member_acquisition_count, 2) AS rnmCac, ROUND(a.new_member_acquisition_count/b.periodic_monthly_active_user_count, 2) AS nmcRate, ROUND(a.new_member_acquisition_gmv/b.periodic_monthly_active_user_gmv, 2) AS nmcGmvRate, ROUND(a.monthly_active_member_gmv/a.monthly_active_member_count, 2) AS pcmaGmv, ROUND(a.next_month_active_member_gmv/a.next_month_active_member_count, 2) AS mpcmaGmv, ROUND(a.new_member_acquisition_gmv/a.new_member_acquisition_count, 2) AS pcnGmv, ROUND(a.next_month_new_member_gmv/a.next_month_new_member_count, 2) AS panGmv, ROUND(a.next_month_active_member_count/a.monthly_active_member_count,2) as masmRate, ROUND(a.next_month_new_member_count/a.new_member_acquisition_count,2) as mrrRate,
ROUND((select avg(cost/exposure_count*1000) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as cpmAvg, ROUND((select avg(monthly_active_member_gmv/cost) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as roiAvg, ROUND((select avg(cost/monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as cacAvg, ROUND((select avg(monthly_active_member_count/b.periodic_monthly_active_user_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as mamcRateAvg, ROUND((select avg(monthly_active_member_gmv/b.periodic_monthly_active_user_gmv) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as mamcGmvRateAvg, ROUND((select avg(new_member_acquisition_gmv/cost) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as rnmRoiAvg, ROUND((select avg(cost/new_member_acquisition_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as rnmCacAvg, ROUND((select avg(new_member_acquisition_count/b.periodic_monthly_active_user_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as nmcRateAvg, ROUND((select avg(new_member_acquisition_gmv/b.periodic_monthly_active_user_gmv) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as nmcGmvRateAvg, ROUND((select avg(monthly_active_member_gmv/monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as pcmaGmvAvg, ROUND((select avg(next_month_active_member_gmv/next_month_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as mpcmaGmvAvg, ROUND((select avg(new_member_acquisition_gmv/new_member_acquisition_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as pnGmvAvg, ROUND((select avg(next_month_new_member_gmv/next_month_new_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as panGmvAvg, ROUND((select avg(next_month_active_member_count/monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as masmRateAvg, ROUND((select avg(next_month_new_member_count/new_member_acquisition_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as mrrRateAvg,
ROUND(a.cost/a.exposure_count*1000, 2)-ROUND((select avg(cost/exposure_count*1000) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point),2) as cpmDiff, ROUND(a.monthly_active_member_gmv/a.cost, 2) - ROUND((SELECT AVG(monthly_active_member_gmv/cost) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS roiDiff, ROUND(a.cost/a.monthly_active_member_count, 2) - ROUND((SELECT AVG(cost/monthly_active_member_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS cacDiff, ROUND(a.monthly_active_member_count/b.periodic_monthly_active_user_count, 2) - ROUND((SELECT AVG(monthly_active_member_count/b.periodic_monthly_active_user_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS mamcRateDiff, ROUND(a.monthly_active_member_gmv/b.periodic_monthly_active_user_gmv, 2) - ROUND((SELECT AVG(monthly_active_member_gmv/b.periodic_monthly_active_user_gmv) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS mamcGmvRateDiff, ROUND(a.new_member_acquisition_gmv/a.cost, 2) - ROUND((SELECT AVG(new_member_acquisition_gmv/cost) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS rnmRoiDiff, ROUND(a.cost/a.new_member_acquisition_count, 2) - ROUND((SELECT AVG(cost/new_member_acquisition_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS rnmCacDiff, ROUND(a.new_member_acquisition_count/b.periodic_monthly_active_user_count, 2) - ROUND((SELECT AVG(new_member_acquisition_count/b.periodic_monthly_active_user_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS nmcRateDiff, ROUND(a.new_member_acquisition_gmv/b.periodic_monthly_active_user_gmv, 2) - ROUND((SELECT AVG(new_member_acquisition_gmv/b.periodic_monthly_active_user_gmv) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS nmcGmvRateDiff, ROUND(a.monthly_active_member_gmv/a.monthly_active_member_count, 2) - ROUND((SELECT AVG(monthly_active_member_gmv/monthly_active_member_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS pcmaGmvDiff, ROUND(a.next_month_active_member_gmv/a.next_month_active_member_count, 2) - ROUND((SELECT AVG(next_month_active_member_gmv/next_month_active_member_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS mpcmaGmvDiff, ROUND(a.new_member_acquisition_gmv/a.new_member_acquisition_count, 2) - ROUND((SELECT AVG(new_member_acquisition_gmv/new_member_acquisition_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS pcnGmvDiff, ROUND(a.next_month_new_member_gmv/a.next_month_new_member_count, 2) - ROUND((SELECT AVG(next_month_new_member_gmv/next_month_new_member_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS panGmvDiff, ROUND(a.next_month_active_member_count/a.monthly_active_member_count, 2) - ROUND((SELECT AVG(next_month_active_member_count/a.monthly_active_member_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS masmRateDiff, ROUND(a.next_month_new_member_count/a.new_member_acquisition_count, 2) - ROUND((SELECT AVG(next_month_new_member_count/a.new_member_acquisition_count) FROM activity_media_slot_analysis WHERE a.media=media AND a.contact_point=contact_point), 2) AS mrrRateDiff FROM activity_media_slot_analysis a,activity_analysis b WHERE a.campaign_id =#{slot.cid} and a.campaign_id=b.campaign_id
and a.contact_point=#{slot.point}
and a.media=#{slot.media}
ORDER BY ${slot.orderfield} ${slot.ordertype}
ORDER BY cost desc
